    private static List<List<Integer>> findTripletsUsingTwoPointer(int[] nums) {
        List<List<Integer>> res = new ArrayList<>();
        Arrays.sort(nums);
        for (int i = 0; i < nums.length; i++) {
            int left = i + 1, right = nums.length - 1;
            while (left < right) {
                int sum = nums[i] + nums[left] + nums[right];
                if (sum == 0) {
//                    if (!res.contains(List.of(nums[i], nums[left], nums[right])))
                        res.add(List.of(nums[i], nums[left], nums[right]));
                    while (left < right && nums[left] == nums[left + 1]) left++;
                    while (left < right && nums[right] == nums[right - 1]) right--;
                    left++;
                    right--;
                }
                if (sum > 0) {
                    right--;
                } else {
                    left++;
                }
            }
        }
        return res;
    }


    private static List<List<Integer>> findTripletsWithSumZero(int[] nums) {
        List<List<Integer>> res = new ArrayList<>();
        HashMap<Integer, ArrayList<Integer>> map = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
            for (int j = i + 1; j < nums.length; j++) {
                int val = -1 * (nums[i] + nums[j]);
                if (map.containsKey(val)) {
                    for (var p : map.get(val)) {
                        var list = new ArrayList<>(List.of(p, i, j));
                        Collections.sort(list);
                        res.add(List.of(p, i, j));

                    }
                }

            }
            map.putIfAbsent(nums[i], new ArrayList<>());
            map.get(nums[i]).add(i);
        }
        return res;
    }
